(function () {
    /** @type {CompositeX.MetaNodeConfig} */
    const nodeConfig = {
      config: {
        name: "MainWorld",
        desc: "Get main world info",
        input: { type: "string" },
        output: { type: "any" },
        options: [
          { name: "expression", type: "string" },
          { 
            name: "type",
            desc: "choose eval if website support it or choose exec which has limits",
            type: "enum",
            enumItems: [{ name: "eval", value: "eval"}, { name: "exec", value: "exec" }],
            default: "eval",
          }
        ],
      },
      run(input, options, context) {
        return context.mainWorld(input || options.expression, { type: options.type })
      },
    }
    return nodeConfig
  })()

(function () {
  const locales = [
    "en",
    "es",
    "ru",
    "pt",
    "zh",
    "ja",
    "ko",
  ]
  /** @type {CompositeX.MetaNodeConfig} */
  const nodeConfig = {
      config: {
          name: "Get Language",
          desc: "Get Language",
          input: { type: "string" },
          output: { type: "string" },
      },
      run(input, options, context) {
          for (const locale of locales) {
            if (input.includes(locale)) {
              return locale
            }
          }
          return 'all'
      }
  }
  return nodeConfig
})()

(function () {
  const hellos = {
    en: "hello",
    es: "hola",
    ru: "здравствуйте",
    pt: "olá",
    zh: "你好",
    ja: "こんにちは",
    ko: "안녕하세요"
  }
  /** @type {CompositeX.MetaNodeConfig} */
  const nodeConfig = {
    config: {
      name: "Say Hello",
      desc: "Say Hello",
      input: {
          name: "language",
          type: "enum",
          enumItems: [
            { name: 'all', value: 'all' },
            { name: 'en', value: 'en' },
            { name: 'es', value: 'es' },
            { name: 'ru', value: 'ru' },
            { name: 'pt', value: 'pt' },
            { name: 'zh', value: 'zh' },
            { name: 'ja', value: 'ja' },
            { name: 'ko', value: 'ko' },
          ],
          default: 'all'
        },
      output: { type: "string" },
    },
    run(input, options, context) {
      const hello = hellos[input]
      return hello || Object.values(hellos).join(', ')
    }
  }
  return nodeConfig
})()
